import React from 'react';

import 'bootstrap/dist/css/bootstrap.min.css';
import 'bootstrap/dist/js/bootstrap.bundle.min';

import DateService from '../services/date.service';
import ProfessionalService from '../services/professional.service'

import DatePicker from "react-datepicker";
import "react-datepicker/dist/react-datepicker.css";

class homeComponent extends React.Component {

    constructor(props) {
        super(props);

        //We add addDays to DATE 
        Date.prototype.addDays = function(days) {
            var date = new Date(this.valueOf());
            date.setDate(date.getDate() + days);
            return date;
        }

        this.state = {
            dates: [],
            id: sessionStorage.getItem("userId"),
            email: sessionStorage.getItem("userEmail"),
            startDate: new Date(),
            endDate: new Date().addDays(7),
            daysToQuery : []
        };
    }

    componentDidMount() {
        this.loadUserId();
        this.showTodayDates();
    }

    async loadUserId() {
        await ProfessionalService.getWithEmail({ email: this.state.email })
            .then(res => {
                if (res.data.success) {
                    const { id } = res.data.data[0];
                    sessionStorage["userId"] = id;
                } else {
                    console.error('Error loading Id');
                }
            })
            .catch(err => {
                console.error('ERROR server' + err);
            });
    }

    async showTodayDates() {
        let myDates = [];

        await this.state.daysToQuery.map((each) => {

            DateService.getDates({ id: this.state.id },{day: each})
            .then(res => {
                if (res.data.success) {
                    const data = res.data.data;
                    myDates.push({ dates: data });
                } else {
                    console.error('Error loading dates service');
                }
            })
            .catch(err => {
                console.error('ERROR server' + err);
            });

        })

        this.setState({dates : myDates});
    }

    async setStartDate(date){
        await this.setState({startDate :date});
        console.log("start: "+this.state.startDate);
        console.log("end: "+this.state.endDate);
        this.setState({daysToQuery: this.getDaysToQuery()});
        console.log("DAYS: "+this.state.daysToQuery);
    }

    async setEndDate(date){
        await this.setState({endDate :date});
        console.log("start: "+this.state.startDate);
        console.log("end: "+this.state.endDate);
        this.setState({daysToQuery: this.getDaysToQuery()});
        console.log("DAYS: "+this.state.daysToQuery);
    }

    filterDate(day){
        return day.getFullYear()+"/"+day.getMonth()+"/"+day.getDate();
    }

    getDaysToQuery(){
        let days = [];
        let eachDay = new Date(this.state.startDate.getTime());
        let i=1;
        const finalDay = new Date(this.state.endDate.getTime());
        while(eachDay.getTime()<= finalDay){
            days.push(this.filterDate(eachDay));
            eachDay = new Date(this.state.startDate.getTime()).addDays(i);
            i++;
        }
        return days;
    }

    render() {
        return (
            <div className="container p-4">
                <div className="row float-right">
                    <div className="col-sm">
                        <table className="table table-hover table-borderless table-sm">
                            <tbody>
                                <tr className="table-info">
                                    <td><strong>Desde: </strong></td>
                                    <td>
                                        <DatePicker
                                            dateFormat="dd/MM/yyyy"
                                            selected={this.state.startDate}
                                            onChange={date => this.setStartDate(date)}
                                            selectsStart
                                            startDate={this.state.startDate}
                                            endDate={this.state.endDate}
                                            minDate={new Date()}
                                        />
                                    </td>
                                </tr>
                                <tr className="table-info">
                                    <td><strong>Hasta: </strong></td>
                                    <td>
                                        <DatePicker
                                            dateFormat="dd/MM/yyyy"
                                            selected={this.state.endDate}
                                            onChange={date => this.setEndDate(date)}
                                            selectsEnd
                                            startDate={this.state.startDate}
                                            endDate={this.state.endDate}
                                            minDate={this.state.startDate}
                                        />
                                    </td>
                                </tr>
                            </tbody>
                        </table>
                    </div>
                </div>

                <div className="row my-5 mx-3">
                    <div className="d-flex justify-content-center">
                        <h2>Pr√≥ximas citas</h2>
                    </div>
                </div>
                <div className="row my-5 mx-3">
                    <table className="table table-bordered table-striped">
                        <thead>
                            <tr>
                                <th scope="col">Fecha</th>
                                <th scope="col">Hora</th>
                                <th scope="col">Correo</th>
                                <th scope="col">Tipo</th>
                            </tr>
                        </thead>
                        <tbody>
                            {this.loadFillData()}
                        </tbody>
                    </table>
                </div>
            </div>
        );
    }

    loadFillData() {
        return this.state.dates.map(each => {
            if (each) {
                //console.log(each);
                return (
                    <tr>
                        <td >{each.date}</td>
                        <td >{each.time}</td>
                        <td >{each.email}</td>
                        <td >{each.dateType.name}</td>
                    </tr>
                );
            }
        });
    }
}

export default homeComponent;
