import React, { Component, useState } from "react";
import TimetableService from '../services/timetable.service';
import $ from 'jquery';

import DatePicker from "react-datepicker";
import "react-datepicker/dist/react-datepicker.css";

class App extends Component {

    constructor(props) {
        super(props);
        this.state = {
            userId: 16, //sessionStorage.getItem("userId");
            tableId: null,
            monday: {
                myDateFrom: new Date(),
                from: "",
                myDateTo: new Date(),
                to: ""
            },
            tuesday: {
                myDateFrom: new Date(),
                from: "",
                myDateTo: new Date(),
                to: ""
            },
            wednesday: {
                myDateFrom: new Date(),
                from: "",
                myDateTo: new Date(),
                to: ""
            },
            thursday: {
                myDateFrom: new Date(),
                from: "",
                myDateTo: new Date(),
                to: ""
            },
            friday: {
                myDateFrom: new Date(),
                from: "",
                myDateTo: new Date(),
                to: ""
            },
            mondayDDBB: null,
            tuesdayDDBB:null,
            wednesdayDDBB:null,
            thursdayDDB:null,
            fridayDDBB:null,
            aux: ""
        };
    }

    componentDidMount() {
        this.queryTimetable();
    }

    queryTimetable(){
        TimetableService.getWithProfessionalId({professionalId: this.state.userId}
            ).then(res => {
            if (res.data.success) {
                const {id,monday,tuesday,wednesday,thursday,friday}=(res.data.data[0]);
                this.state.tableId=id;
                console.log(res.data.data[0]);

                if(monday!=null){
                    this.state.mondayDDBB=monday;
                    $('#mondayRow').text(monday);
                }
                if(tuesday!=null){
                    this.state.tuesdayDDBB=tuesday;
                    $('#tuesdayRow').text(tuesday);
                }
                if(wednesday!=null){
                    this.state.wednesdayDDBB=wednesday;
                    $('#wednesdayRow').text(wednesday);
                }
                if(thursday!=null){
                    this.state.thursdayDDB=thursday;
                    $('#thursdayRow').text(thursday);
                }
                if(friday!=null){
                    this.state.fridayDDBB=friday;
                    $('#fridayRow').text(friday);
                }
            } else {
                console.error('Error quering timetable');
            }
        })
        .catch(err => {
            console.error('ERROR server' + err);
        });
    }

    render() {
        return (
            <div className="container">
                <div className="row my-5 mx-3" >
                    <table className="table table-bordered table-striped">
                        <thead>
                            <tr>
                                <th scope="col">Lunes</th>
                                <th scope="col">Martes</th>
                                <th scope="col">Mi√©rcoles</th>
                                <th scope="col">Jueves</th>
                                <th scope="col">Viernes</th>
                            </tr>
                        </thead>
                        <tbody>
                            <tr>
                                <td id="mondayRow"></td>
                                <td id="tuesdayRow"></td>
                                <td id="wednesdayRow"></td>
                                <td id="thursdayRow"></td>
                                <td id="fridayRow"></td>
                            </tr>
                        </tbody>
                    </table>
                </div>

                <div className="row my-3">
                    <div className="col-lg-12">
                        <div className="card">
                            <div className="card-body">
                                <div class="container">
                                    <div class="row">
                                        <div class="col-sm">
                                            <h3>Lunes</h3>
                                        </div>
                                        <div class="col-sm">
                                            <label className="float-left">Desde </label>
                                            <div className="w-10">
                                                <DatePicker
                                                    selected={this.state.monday.myDateFrom}
                                                    onChange={time => this.handleChange(time, 'mondayFrom')}
                                                    showTimeSelect
                                                    showTimeSelectOnly
                                                    timeIntervals={20}
                                                    timeCaption="Time"
                                                    dateFormat="h:mm aa"
                                                />
                                            </div>
                                        </div>
                                        <div class="col-sm">
                                            <label className="float-left">Hasta </label>
                                           <DatePicker
                                                selected={this.state.monday.myDateTo}
                                                onChange={time => this.handleChange(time, 'mondayTo')}
                                                showTimeSelect
                                                showTimeSelectOnly
                                                timeIntervals={20}
                                                timeCaption="Time"
                                                dateFormat="h:mm aa"
                                            />
                                        </div>
                                        <div class="col-sm">
                                            <button type="button" class="btn btn-primary m-1"
                                                onClick={() => this.addPeriod('monday')}>A√±adir</button>
                                            <button type="button" class="btn btn-danger m-1">Vaciar</button>
                                        </div>
                                    </div>
                                </div>
                            </div>
                        </div>
                    </div>
                </div>

                <div className="row my-3">
                    <div className="col-lg-12">
                        <div className="card">
                            <div className="card-body">
                                <div class="container">
                                    <div class="row">
                                        <div class="col-sm">
                                            <h3>Martes</h3>
                                        </div>
                                        <div class="col-sm">
                                            <label className="float-left">Desde </label>
                                            <div className="w-10">
                                                <DatePicker
                                                    selected={this.state.tuesday.myDateFrom}
                                                    onChange={time => this.handleChange(time, 'tuesdayFrom')}
                                                    showTimeSelect
                                                    showTimeSelectOnly
                                                    timeIntervals={20}
                                                    timeCaption="Time"
                                                    dateFormat="h:mm aa"
                                                />
                                            </div>
                                        </div>
                                        <div class="col-sm">
                                            <label className="float-left">Hasta </label>
                                           <DatePicker
                                                selected={this.state.tuesday.myDateTo}
                                                onChange={time => this.handleChange(time, 'tuedayTo')}
                                                showTimeSelect
                                                showTimeSelectOnly
                                                timeIntervals={20}
                                                timeCaption="Time"
                                                dateFormat="h:mm aa"
                                            />
                                        </div>
                                        <div class="col-sm">
                                            <button type="button" class="btn btn-primary m-1">A√±adir</button>
                                            <button type="button" class="btn btn-danger m-1">Vaciar</button>
                                        </div>
                                    </div>
                                </div>
                            </div>
                        </div>
                    </div>
                </div>

                <div className="row my-3">
                    <div className="col-lg-12">
                        <div className="card">
                            <div className="card-body">
                                <div class="container">
                                    <div class="row">
                                        <div class="col-sm">
                                            <h3>Mi√©rcoles</h3>
                                        </div>
                                        <div class="col-sm">
                                            <label className="float-left">Desde </label>
                                            <div className="w-10">
                                                <DatePicker
                                                    selected={this.state.wednesday.myDateFrom}
                                                    onChange={time => this.handleChange(time, 'wednesdayFrom')}
                                                    showTimeSelect
                                                    showTimeSelectOnly
                                                    timeIntervals={20}
                                                    timeCaption="Time"
                                                    dateFormat="h:mm aa"
                                                />
                                            </div>
                                        </div>
                                        <div class="col-sm">
                                            <label className="float-left">Hasta </label>
                                           <DatePicker
                                                selected={this.state.wednesday.myDateTo}
                                                onChange={time => this.handleChange(time, 'wednesdayTo')}
                                                showTimeSelect
                                                showTimeSelectOnly
                                                timeIntervals={20}
                                                timeCaption="Time"
                                                dateFormat="h:mm aa"
                                            />
                                        </div>
                                        <div class="col-sm">
                                            <button type="button" class="btn btn-primary m-1">A√±adir</button>
                                            <button type="button" class="btn btn-danger m-1">Vaciar</button>
                                        </div>
                                    </div>
                                </div>
                            </div>
                        </div>
                    </div>
                </div>

                <div className="row my-3">
                    <div className="col-lg-12">
                        <div className="card">
                            <div className="card-body">
                                <div class="container">
                                    <div class="row">
                                        <div class="col-sm">
                                            <h3>Jueves</h3>
                                        </div>
                                        <div class="col-sm">
                                            <label className="float-left">Desde </label>
                                            <div className="w-10">
                                                <DatePicker
                                                    selected={this.state.thursday.myDateFrom}
                                                    onChange={time => this.handleChange(time, 'thursdayFrom')}
                                                    showTimeSelect
                                                    showTimeSelectOnly
                                                    timeIntervals={20}
                                                    timeCaption="Time"
                                                    dateFormat="h:mm aa"
                                                />
                                            </div>
                                        </div>
                                        <div class="col-sm">
                                            <label className="float-left">Hasta </label>
                                           <DatePicker
                                                selected={this.state.thursday.myDateTo}
                                                onChange={time => this.handleChange(time, 'thursdayTo')}
                                                showTimeSelect
                                                showTimeSelectOnly
                                                timeIntervals={20}
                                                timeCaption="Time"
                                                dateFormat="h:mm aa"
                                            />
                                        </div>
                                        <div class="col-sm">
                                            <button type="button" class="btn btn-primary m-1">A√±adir</button>
                                            <button type="button" class="btn btn-danger m-1">Vaciar</button>
                                        </div>
                                    </div>
                                </div>
                            </div>
                        </div>
                    </div>
                </div>

                <div className="row my-3">
                    <div className="col-lg-12">
                        <div className="card">
                            <div className="card-body">
                                <div class="container">
                                    <div class="row">
                                        <div class="col-sm">
                                            <h3>Viernes</h3>
                                        </div>
                                        <div class="col-sm">
                                            <label className="float-left">Desde </label>
                                            <div className="w-10">
                                                <DatePicker
                                                    selected={this.state.friday.myDateFrom}
                                                    onChange={time => this.handleChange(time, 'fridayFrom')}
                                                    showTimeSelect
                                                    showTimeSelectOnly
                                                    timeIntervals={20}
                                                    timeCaption="Time"
                                                    dateFormat="h:mm aa"
                                                />
                                            </div>
                                        </div>
                                        <div class="col-sm">
                                            <label className="float-left">Hasta </label>
                                           <DatePicker
                                                selected={this.state.monday.myDateTo}
                                                onChange={time => this.handleChange(time, 'fridayTo')}
                                                showTimeSelect
                                                showTimeSelectOnly
                                                timeIntervals={20}
                                                timeCaption="Time"
                                                dateFormat="h:mm aa"
                                            />
                                        </div>
                                        <div class="col-sm">
                                            <button type="button" class="btn btn-primary m-1">A√±adir</button>
                                            <button type="button" class="btn btn-danger m-1">Vaciar</button>
                                        </div>
                                    </div>
                                </div>
                            </div>
                        </div>
                    </div>
                </div>
            </div>
        );
    }

    handleChange = async (time, day) => {
        const filteredTime = await String(time).match(/\d{2}:\d{2}/g)[0];
        switch (day) {
            case "mondayFrom":
                this.state.monday.myDateFrom = time;
                this.state.monday.from = filteredTime;
                break;
            case "mondayTo":
                this.state.monday.myDateTo = time;
                this.state.monday.to = filteredTime;
                break;

            default:
                break;
        }
        //This is just to render the changes
        this.setState({ aux: ":)" })
    };

    addPeriod(day){
        const week = {
            id: this.state.tableId,
            professionalId: this.state.userId,
            monday:this.state.mondayDDBB,
            tuesday:this.state.tuesdayDDBB,
            wednesday:this.state.wednesdayDDBB,
            thursday:this.state.thursdayDDB,
            friday:this.state.fridayDDBB
        }

        if(this.state.tableId==null){
            console.log('ENTRO DIRECTAMENTE');
            TimetableService.create({
                    professionalId: this.state.userId,
                    monday:null,
                    tuesday:null,
                    wednesday:null,
                    thursday:null,
                    friday:null
            })
            .then(res => {
                this.setState({tableId: res.data.id});
            })
            .catch(
                console.error("error creando timetable")
            );
        }
        console.log('ENTRO');
        switch (day) {
            case 'monday':
                week.monday = this.state.monday.from+"-"+this.state.monday.to;
                break;

            default:
                break;
        }
        TimetableService.update(week).then((res)=> console.log(res));
        this.queryTimetable;
    }

}

export default App;
